import java.util.*;

public class DNA {
    public static void main(String[] args) {
        // The Dna method
        String Dna = "";

        Dna = Dna.toUpperCase();

        ArrayList<String> DnaCodons = new ArrayList<String>();
        DnaCodons = multiple(Dna);
        int size = DnaCodons.size();
        int high60Count = 0;
        System.out.println("This Dna has " + size + " Codons.");
        int cgcountmore = 0;

        // creating the project
        for (String DnaCodon : DnaCodons) {
            System.out.println(DnaCodon);
            System.out.println((DnaCodon.length()/3));
            if ((DnaCodon.length()/3) > 60) {
                high60Count++;
            }
            float tmp = CGcount(DnaCodon);
            System.out.println(tmp);
            if (tmp > 0.35) {
                cgcountmore++;
            }

        }


        System.out.println("The amount of more than 60 letters is " + high60Count);
        codonCount(Dna);
        System.out.println(cgcountmore);
        ctgcount(Dna);

    }

    // Counting CTG
    public static void codonCount(String DNA) {
        int count = 0;
        int StartIndex = 0;
        while(true) {
            int index = -1;
            index = DNA.indexOf("CTG", StartIndex);
            if (index != -1) {
                count ++;
                StartIndex += index + 1;
            } else {
                break;
            }
        }
        System.out.println(count);
    }

    // Making it support multiple dnas
    public static ArrayList<String> multiple(String Dna) {
        int StartIndex = 0;
        ArrayList<String> DnaCodonstmp = new ArrayList<String>();
        while (true) {
            StartIndex = Dna.indexOf("ATG", StartIndex);
            if (StartIndex == -1) {
                break;
            }

            // adding the codon to the array list
            String newCodon = findGene(Dna, StartIndex);    
            if (newCodon == "") {
                break;
            }
            DnaCodonstmp.add(newCodon);
            StartIndex += newCodon.length();
        }

        return DnaCodonstmp;
    }

    // Making the base of the gene chcking
    public static String findGene(String Dna, int StartIndex) {
        // getting the tree values
        int firstTAA = getCodon(Dna, StartIndex, "TAA");
        int firstTAG = getCodon(Dna, StartIndex, "TAG");
        int firstTGA = getCodon(Dna, StartIndex, "TGA");

        // getting the smallest value of them
        int smallest = Math.min(Math.min(firstTAA, firstTAG), firstTGA);
        if (smallest == Dna.length()) {
            return "";
        }
        return Dna.substring(StartIndex, smallest + 3);
    }

    public static void ctgcount(String Dna) {
        int startPos = 0;
        int pos = 0;
        int count = 0;
        while (pos != -1) {
            pos = Dna.indexOf("CTG", startPos);
            startPos = pos + 3;
            count++;
        }
        System.out.println("CTG count: " + count);
    }

    // method to check the codon
    public static int getCodon(String Dna, int StartIndex, String StopCodon) {
        int EndIndex = 0;
        int ATGIndex = StartIndex;
        while (true) {
            EndIndex = Dna.indexOf(StopCodon, StartIndex + 1);
            if (EndIndex == -1) {
                return Dna.length();
            }
            int diff = EndIndex - ATGIndex;
            if (diff % 3 == 0) {
                return EndIndex;
            }
            StartIndex = EndIndex + 1;
        }
    }

    public static float CGcount(String Dna) {
        Dna = Dna.toUpperCase();
        // getting the full count of the dna
        int FullCount = Dna.length();

        // getting the count of c and g
        int CandGcount = 0;
        for (int i = 0; i < FullCount; i++) {
            if (Dna.charAt(i) == 'C' || Dna.charAt(i) == 'G' ) {
                CandGcount++;
            }
        }

        float cgRario = (float) CandGcount / FullCount;
        return cgRario;
    }
}